            guard: ({ event, context }) => {
              return event.guess === context.number;
            },
            target: 'success',
            actions: assign({ message: () => 'Угадал!' }),
          },
          {
            guard: ({ context }) => {
              return context.attempts <= 1;
            },
            target: 'failure',
            actions: assign({ message: () => 'Попытки закончились!' }),
          },
          {
            actions: assign({
              attempts: ({ context }) => {
                return context.attempts - 1;
              },
              message: ({ event, context }) => {
                return event.guess < context.number
                  ? 'Слишком мало'
                  : 'Слишком много';
              },
            }),
          },
        ],
      },
    },
    success: { type: 'final' },
    failure: { type: 'final' },
  },
});

export { guessMachine };
